mapper.update:
UPDATE product

	
	prod_name=#{prodName}
	
	
	,prod_detail=#{prodDetail}
	
	
	,manufacture_Day=#{manuDate}
	
	
	,price=#{price:NUMERIC}
	
	
	,image_file=#{fileName}
	
	
	,quantity=#{quantity}, stock=#{quantity}
	
	
	, stock=#{stock}
	

WHERE prod_no=#{prodNo}

mapper.select:
SELECT * FROM ( SELECT inner_table.* , ROWNUM AS row_seq FROM ( SELECT p.prod_no, p.prod_name, p.prod_detail, p.manufacture_Day, p.price, p.image_file, p.reg_date,stock, quantity FROM product p
p.deleted IS NULL
AND p.prod_no LIKE '%'||#{searchKeyword}||'%'
AND p.prod_name LIKE '%'||#{searchKeyword}||'%'
AND p.stock > 0
AND p.price BETWEEN #{searchKeyword1} AND #{searchKeyword2}
AND p.price BETWEEN #{searchKeyword2} AND #{searchKeyword1}
AND p.price <= #{searchKeyword1};
ORDER BY p.price
ORDER BY p.price DESC
ORDER BY p.prod_no
ORDER BY p.reg_date DESC
ORDER BY p.prod_no
) inner_table WHERE ROWNUM <= #{endRowNum} ) WHERE row_seq BETWEEN #{startRowNum} AND #{endRowNum}

SELECT COUNT(*) FROM( SELECT p.prod_no, p.prod_name, p.prod_detail, p.manufacture_Day, p.price, p.image_file, p.reg_date, stock, quantity FROM product p
p.deleted IS NULL
AND p.prod_no LIKE '%'||#{searchKeyword}||'%'
AND p.prod_name LIKE '%'||#{searchKeyword}||'%'
AND p.stock > 0
AND p.price BETWEEN #{searchKeyword1} AND #{searchKeyword2}
AND p.price BETWEEN #{searchKeyword2} AND #{searchKeyword1}
AND p.price <= #{searchKeyword1};
ORDER BY p.price
ORDER BY p.price DESC
ORDER BY p.prod_no
ORDER BY p.reg_date DESC
) countTable
